import React, { useRef, useState, useEffect } from 'react';
import './App.css';

import firebase from 'firebase/compat/app';
import 'firebase/compat/firestore';
import 'firebase/compat/auth';

firebase.initializeApp({
  apiKey: "AIzaSyAZU3vY4jE4w1jZw4MmERT2styneahJVsU",
  authDomain: "shop-green-a3ae3.firebaseapp.com",
  projectId: "shop-green-a3ae3",
  storageBucket: "shop-green-a3ae3.appspot.com",
  messagingSenderId: "714877199071",
  appId: "1:714877199071:web:980b9b82017b86e5513f6a"
})

const auth = firebase.auth();
const firestore = firebase.firestore();


function App() {

  const [items, setItems] = useState([]);

  const itemsRef = firebase.firestore().collection("items");

  function getItems() {
    itemsRef.onSnapshot((querySnapshot) => {
      const items = [];
      querySnapshot.forEach((doc) => {
        items.push(doc.data());
        console.log(doc.data());
      });
      setItems(items);
    });
  }

  useEffect( () => {
    getItems();
  });

  return (
    <div>
      <h1>Items:</h1>
      {items.map((items) => (
        <div key={items.id}>
          <h2>{items.name}</h2>
          <h3>{items.price}</h3>
          <h4>{items.description}</h4>
          </div>
      ))}
    </div>
  );
}

export default App;
